import React, { useState } from "react";
import DatePicker from 'react-datepicker';
import axios from "axios";
import "react-datepicker/dist/react-datepicker.css";
import {Link, useNavigate} from "react-router-dom";

export default function Registro() {
    const [formData, setFormData] = useState({
        dni: "",
        nombre: "",
        apellido: "",
        fechaNacimiento: new Date(),
        distrito: "",
        departamento: "",
        fechaAfiliacion: new Date(),
        email: "",
      });
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
      };
    
      const handleDateChange = (date, name) => {
        setFormData({ ...formData, [name]: date });
      };
    
      const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          // Realiza una solicitud POST al backend para crear el registro
          const response = await axios.post("/registro", formData);
          console.log("Registro exitoso", response.data);
          // Puedes redirigir al usuario a una página de confirmación o realizar otras acciones aquí
        } catch (error) {
          console.error("Error en el registro", error);
          // Puedes mostrar un mensaje de error al usuario o tomar otras acciones
        }
      };
    
      return (
        <div className="d-flex justify-content-center align-items-center bg-primary vh-100">
          <h2>Registro</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="dni">DNI:</label>
              <input type="text" id="dni" name="dni" value={formData.dni} onChange={handleChange} />
            </div>
            <div>
              <label htmlFor="nombre">Nombre:</label>
              <input type="text" id="nombre" name="nombre" value={formData.nombre} onChange={handleChange} />
            </div>
            {/* Agrega más campos de formulario para otros atributos */}
            <div>
              <label htmlFor="fechaNacimiento">Fecha de Nacimiento:</label>
              <DatePicker
                id="fechaNacimiento"
                selected={formData.fechaNacimiento}
                onChange={(date) => handleDateChange(date, "fechaNacimiento")}
              />
            </div>
            {/* Agrega más campos de formulario para otros atributos */}
            <button type="submit">Registrar</button>
          </form>
        </div>
      );
}
