import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Inicio() {
    const [productos, setProductos] = useState([]);
    const [carrito, setCarrito] = useState([]);
    const [carritoAbierto, setCarritoAbierto] = useState(false);
    const [cantidad, setCantidad] = useState(1); // Estado para la cantidad

    useEffect(() => {
        axios.get("http://localhost:8081/productos")
            .then((response) => {
                setProductos(response.data);
            })
            .catch((error) => {
                console.error("Error al obtener los productos:", error);
            });
    }, []);

    const toggleCarrito = () => {
        setCarritoAbierto(!carritoAbierto);
    };

    const agregarAlCarrito = (producto) => {
        const productoEnCarrito = carrito.find((item) => item.codigo === producto.codigo);
        if (productoEnCarrito) {
            productoEnCarrito.cantidad += cantidad;
            setCarrito([...carrito]);
        } else {
            setCarrito([...carrito, { ...producto, cantidad }]);
        }
        setCantidad(1); // Restablecer la cantidad a 1 después de agregar al carrito
    };

    const eliminarDelCarrito = (producto) => {
        const nuevoCarrito = carrito.filter((item) => item.codigo !== producto.codigo);
        setCarrito(nuevoCarrito);
    };

    const carritoStyle = {
        height: carritoAbierto ? "50%" : "0",
        overflow: "hidden",
        transition: "height 0.3s ease",
    };

    return (
        <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
            <header style={{ backgroundColor: "#007bff", color: "white", padding: "10px", textAlign: "center" }}>
                <h1>Hola, mundo</h1>
                <button onClick={toggleCarrito}>Carrito</button>
            </header>
            <div style={{ flex: "1", padding: "20px" }}>
                <h2>Productos:</h2>
                <table style={{ width: "100%" }}>
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Precio</th>
                            <th>Stock</th>
                            <th>Cantidad</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {productos.map((producto) => (
                            <tr key={producto.codigo}>
                                <td>{producto.nombre}</td>
                                <td>${producto.precio}</td>
                                <td>{producto.stock}</td>
                                <td>
                                    <input
                                        type="number"
                                        value={cantidad}
                                        min="1"
                                        max={producto.stock}
                                        onChange={(e) => setCantidad(parseInt(e.target.value))}
                                        style={{ width: "50px" }}
                                    />
                                </td>
                                <td>
                                    <button onClick={() => agregarAlCarrito(producto)}>Agregar al Carrito</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="carrito-dropdown" style={carritoStyle}>
                <div className="carrito-contenido">
                    <h3>Carrito de Compras</h3>
                    {carrito.map((producto) => (
                        <div key={producto.codigo}>
                            {producto.nombre} - Precio: ${producto.precio} - Cantidad: {producto.cantidad}
                            <button onClick={() => eliminarDelCarrito(producto)}>Eliminar</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
