import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Importa useNavigate
import axios from "axios";

export default function Login() {
  const [correo, setCorreo] = useState("");
  const [dni, setDni] = useState("");
  const navigate = useNavigate(); // Obtiene la función navigate

  const handleCorreoChange = (e) => {
    setCorreo(e.target.value);
  };

  const handleDniChange = (e) => {
    setDni(e.target.value);
  };

  const handleLogin = () => {
    axios
      .post("/ruta-de-autenticacion", { correo, dni })
      .then((response) => {
        const userData = response.data;
        // Autenticación exitosa
        // Realiza acciones de autenticación exitosa, como guardar el token de sesión en el estado o las cookies
        // Luego redirige al usuario a la página principal (por ejemplo, "/home")
        navigate("/home");
      })
      .catch((error) => {
        // Handle errors, show error messages, etc.
        console.error("Error de autenticación:", error);
      });
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      <form>
        <div>
          <label htmlFor="correo">Correo Electrónico:</label>
          <input
            type="email"
            id="correo"
            value={correo}
            onChange={handleCorreoChange}
          />
        </div>
        <div>
          <label htmlFor="dni">DNI:</label>
          <input
            type="text"
            id="dni"
            value={dni}
            onChange={handleDniChange}
          />
        </div>
        <button type="button" onClick={handleLogin}>
          Iniciar Sesión
        </button>
      </form>
    </div>
  );
}
