import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import Cabecera from "./Cabecera"; // Importa el componente de cabecera
import PieDePagina from "./PieDePagina"; // Importa el componente de pie de pÃ¡gina

export default function Inicio() {
    const [productos, setProductos] = useState([]);
    const [carrito, setCarrito] = useState([]);
    const [carritoAbierto, setCarritoAbierto] = useState(false);
    const [cantidad, setCantidad] = useState(1);
    const [productoSeleccionado, setProductoSeleccionado] = useState(null);

    useEffect(() => {
        axios.get("http://localhost:8081/productos")
            .then((response) => {
                setProductos(response.data);
            })
            .catch((error) => {
                console.error("Error al obtener los productos:", error);
            });
    }, []);

    const toggleCarrito = () => {
        setCarritoAbierto(!carritoAbierto);
    };

    const agregarAlCarrito = (producto) => {
        const productoEnCarrito = carrito.find((item) => item.codigo === producto.codigo);
        if (productoEnCarrito) {
            productoEnCarrito.cantidad += cantidad;
            setCarrito([...carrito]);
        } else {
            setCarrito([...carrito, { ...producto, cantidad }]);
        }
        setCantidad(1);
    };

    const mostrarDescripcionProducto = (producto) => {
        setProductoSeleccionado(producto);
    };

    const cerrarDescripcionProducto = () => {
        setProductoSeleccionado(null);
    };

    const eliminarDelCarrito = (producto) => {
        const nuevoCarrito = carrito.filter((item) => item.codigo !== producto.codigo);
        setCarrito(nuevoCarrito);
    };

    return (
        <div>
            <div className="container" style={{ paddingTop: "20px" }}>
                <h1 className="text-center">SCRIPT MARKET</h1>
                <div className="row">
                    {productos.map((producto) => (
                        <div key={producto.codigo} className="col-lg-3 col-md-4 col-sm-6">
                            <div className="card mb-4">
                                <img src={producto.imagen} className="card-img-top" alt={producto.nombre} />
                                <div className="card-body">
                                    <h5 className="card-title">{producto.nombre}</h5>
                                    <p className="card-text">${producto.precio}</p>
                                    <p className="card-text">Stock: {producto.stock}</p>
                                    <input
                                        type="number"
                                        value={cantidad}
                                        min="1"
                                        max={producto.stock}
                                        onChange={(e) => setCantidad(parseInt(e.target.value))}
                                        style={{ width: "50px" }}
                                    />
                                    <button className="btn btn-primary" onClick={() => agregarAlCarrito(producto)}>Agregar al Carrito</button>
                                    <button className="btn btn-info" onClick={() => mostrarDescripcionProducto(producto)}>Ver DescripciÃ³n</button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
                <div className="carrito-dropdown">
                    <button className="btn btn-success" onClick={toggleCarrito}>Carrito</button>
                    {carritoAbierto && (
                        <div className="carrito-contenido">
                            <h3>Carrito de Compras</h3>
                            {carrito.map((producto) => (
                                <div key={producto.codigo} className="carrito-item">
                                    <div className="carrito-info">
                                        <p>{producto.nombre}</p>
                                        <p>Precio: ${producto.precio}</p>
                                        <p>Cantidad: {producto.cantidad}</p>
                                    </div>
                                    <button className="btn btn-danger" onClick={() => eliminarDelCarrito(producto)}>Eliminar</button>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
            {productoSeleccionado && (
                <div className="modal" tabIndex="-1" role="dialog" style={{ display: "block" }}>
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">DescripciÃ³n del Producto</h5>
                                <button type="button" className="close" onClick={cerrarDescripcionProducto}>
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                <p>Nombre: {productoSeleccionado.nombre}</p>
                                <p>Precio: ${productoSeleccionado.precio}</p>
                                <p>Stock: {productoSeleccionado.stock}</p>
                                <p>DescripciÃ³n: {productoSeleccionado.descripcion}</p>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" onClick={cerrarDescripcionProducto}>Cerrar</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
