import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

export default function Login() {
    const [correo, setCorreo] = useState("");
    const [dni, setDni] = useState("");
    const [loggedIn, setLoggedIn] = useState(false);
  
    const handleLogin = () => {
      axios
        .post("/ruta-de-autenticacion", { correo, dni })
        .then((response) => {
          const userData = response.data;
          // Autenticación exitosa
          // Realiza acciones de autenticación exitosa, como guardar el token de sesión en el estado o las cookies
          // Luego actualiza el estado para redirigir
          setLoggedIn(true);
        })
        .catch((error) => {
          // Handle errors
          console.error("Error de autenticación:", error);
        });
    };
  
    if (loggedIn) {
      // Redirige al usuario a la página principal (por ejemplo, "/home")
      return <Redirect to="/home" />;
    }

    return (
        <div>
            <h2>Iniciar Sesión</h2>
            <form>
                <div>
                    <label htmlFor="correo">Correo Electrónico:</label>
                    <input
                        type="email"
                        id="correo"
                        value={correo}
                        onChange={handleCorreoChange}
                    />
                </div>
                <div>
                    <label htmlFor="dni">DNI:</label>
                    <input
                        type="text"
                        id="dni"
                        value={dni}
                        onChange={handleDniChange}
                    />
                </div>
                <button type="button" onClick={handleLogin}>
                    Iniciar Sesión
                </button>
            </form>
        </div>
    );
}
