import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Inicio() {
    const [productos, setProductos] = useState([]);
    const [carrito, setCarrito] = useState([]);
    const [carritoAbierto, setCarritoAbierto] = useState(false);
    const [cantidad, setCantidad] = useState(1);

    useEffect(() => {
        // Cambia la URL para obtener productos desde tu backend
        axios.get("http://localhost:8081/productos")
            .then((response) => {
                setProductos(response.data);
            })
            .catch((error) => {
                console.error("Error al obtener los productos:", error);
            });
    }, []);

    const toggleCarrito = () => {
        setCarritoAbierto(!carritoAbierto);
    };

    const agregarAlCarrito = (producto) => {
        const dniCliente = "dni_del_cliente"; // Reemplaza con el DNI real del cliente
        const productoCodigo = producto.codigo; // Código del producto
        const cantidad = cantidad; // La cantidad que el cliente desea agregar
    
        // Realiza una solicitud POST al servidor para agregar el producto al carrito
        axios.post("http://localhost:8081/carrito", {
            cliente_dni: dniCliente,
            producto_codigo: productoCodigo,
            cantidad: cantidad
        })
        .then((response) => {
            // Maneja la respuesta del servidor, por ejemplo, actualiza el carrito local
            console.log(response.data);
            // Actualiza el carrito local si es necesario
        })
        .catch((error) => {
            console.error("Error al agregar producto al carrito:", error);
        });
    };

    const eliminarDelCarrito = (producto) => {
        // Aquí debes enviar una solicitud DELETE al backend para eliminar un producto del carrito
        // Debes proporcionar el ID del producto en la solicitud
        axios.delete(`http://localhost:8081/carrito/${producto.id}`)
            .then(() => {
                // Actualiza el carrito local si la solicitud se completó con éxito
                const nuevoCarrito = carrito.filter((item) => item.codigo !== producto.codigo);
                setCarrito(nuevoCarrito);
            })
            .catch((error) => {
                console.error("Error al eliminar producto del carrito:", error);
            });
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            <div style={{ flex: "50%", overflowY: "auto", padding: "20px" }}>
                <h1>SCRIPT MARKET</h1>
                <h2>Productos:</h2>
                <table style={{ width: "100%" }}>
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Precio</th>
                            <th>Stock</th>
                            <th>Cantidad</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {productos.map((producto) => (
                            <tr key={producto.codigo}>
                                <td>{producto.nombre}</td>
                                <td>${producto.precio}</td>
                                <td>{producto.stock}</td>
                                <td>
                                    <input
                                        type="number"
                                        value={cantidad}
                                        min="1"
                                        max={producto.stock}
                                        onChange={(e) => setCantidad(parseInt(e.target.value))}
                                        style={{ width: "50px" }}
                                    />
                                </td>
                                <td>
                                    <button onClick={() => agregarAlCarrito(producto)}>Agregar al Carrito</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div style={{ flex: "50%", position: "relative" }}>
                <div className="carrito-dropdown">
                    <button onClick={toggleCarrito} style={{ width: "100%" }}>Carrito</button>
                    {carritoAbierto && (
                        <div className="carrito-contenido">
                            <h3>Carrito de Compras</h3>
                            {carrito.map((producto) => (
                                <div key={producto.codigo}>
                                    {producto.nombre} - Precio: ${producto.precio} - Cantidad: {producto.cantidad}
                                    <button onClick={() => eliminarDelCarrito(producto)}>Eliminar</button>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}
