import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Inicio() {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Realizar la solicitud HTTP GET para obtener los productos desde el servidor
    axios.get("http://localhost:8081/productos")
      .then((response) => {
        // Almacenar los productos en el estado del componente
        setProductos(response.data);
      })
      .catch((error) => {
        console.error("Error al obtener los productos:", error);
      });
  }, []); // El array vacío [] asegura que esta solicitud se haga solo una vez al cargar el componente

  return (
    <div>
      <h1>Hola, mundo</h1>
      <h2>Productos:</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Stock</th>
          </tr>
        </thead>
        <tbody>
          {productos.map((producto) => (
            <tr key={producto.codigo}>
              <td>{producto.nombre}</td>
              <td>${producto.precio}</td>
              <td>{producto.stock}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
