import React from "react";

export default function Compra({ carrito, eliminarDelCarrito }) {
  const handlePayment = (e) => {
    e.preventDefault();
    // Lógica para procesar el pago aquí
    console.log("Procesando el pago...");
  };

  return (
    <div className="container" style={{ paddingTop: "20px" }}>
      <h1 className="text-center title">SCRIPT MARKET</h1>
      <div className="row">
        <div className="col-md-8">
          {/* Resto del formulario de compra */}
        </div>
        <div className="col-md-4">
          <h2 className="cart-title">Carrito de Compras:</h2>
          <div className="carrito-dropdown">
            <button className="btn btn-green" onClick={handlePayment}>
              Comprar
            </button>
            <button className="btn btn-orange">
              Desplegar Carrito
            </button>
            {carrito.length > 0 && (
              <div className="carrito-contenedor">
                <div className="carrito-contenido">
                  <h3 className="cart-title">Carrito de Compras</h3>
                  {carrito.map((producto) => (
                    <div key={producto.codigo} className="carrito-item">
                      <div className="carrito-info">
                        <p className="cart-product-name">{producto.nombre}</p>
                        <p className="price">Precio: ${producto.precio}</p>
                        <p className="cart-quantity">Cantidad: {producto.cantidad}</p>
                      </div>
                      <button className="btn btn-black" onClick={() => eliminarDelCarrito(producto)}>
                        Eliminar
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      {/* Resto del contenido de compra */}
      <div className="col-md-8">
        <h2>Detalles de Pago</h2>
        <form onSubmit={handlePayment}>
          {/* Campos de información de pago (número de tarjeta, fecha de vencimiento, etc.) */}
          {/* ... */}
          <h3>Dirección de Facturación</h3>
          <div className="form-group">
            <textarea
              className="form-control"
              value={billingAddress}
              onChange={(e) => setBillingAddress(e.target.value)}
              placeholder="Ingrese su dirección de facturación"
            />
          </div>
          <h3>Opciones de Envío</h3>
          <div className="form-group">
            <select
              className="form-control"
              value={shippingOption}
              onChange={(e) => setShippingOption(e.target.value)}
            >
              <option value="standard">Envío Estándar (3-5 días)</option>
              <option value="express">Envío Express (1-2 días)</option>
            </select>
          </div>
          <button type="submit" className="btn btn-primary">
            Pagar
          </button>
        </form>
      </div>

    </div>
  );
}
