import React, { useState, useContext } from "react";
import { CarritoContext } from "./Compra"; // Importa el contexto del carrito

export default function Compra() {
  const { carrito, setCarrito } = useContext(CarritoContext);

  const [cardNumber, setCardNumber] = useState("");
  const [expiryDate, setExpiryDate] = useState("");
  const [cvv, setCvv] = useState("");
  const [billingAddress, setBillingAddress] = useState("");
  const [shippingOption, setShippingOption] = useState("standard");

  const handlePayment = (e) => {
    e.preventDefault();

    // Agrega aquí la lógica para procesar el pago y los datos de envío.

    // Limpia los campos después de procesar el pago
    setCardNumber("");
    setExpiryDate("");
    setCvv("");
    setBillingAddress("");
  };

  return (
    <div className="container">
      <h2>Detalles de Pago</h2>
      {/* Muestra el carrito de compras aquí */}
      <h2>Carrito de Compras:</h2>
      <ul>
        {carrito.map((producto) => (
          <li key={producto.codigo}>
            {producto.nombre} - Cantidad: {producto.cantidad}
          </li>
        ))}
      </ul>

      <form onSubmit={handlePayment}>
        {/* Resto del código de pago */}
        <button type="submit" className="btn btn-primary">
          Pagar
        </button>
      </form>
    </div>
  );
}
