import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Inicio() {
    const [productos, setProductos] = useState([]);
    const [carrito, setCarrito] = useState([]);
    const [carritoAbierto, setCarritoAbierto] = useState(false);
    const [cantidad, setCantidad] = useState(1);
    useEffect(() => {
        // Realizar la solicitud HTTP GET para obtener los productos desde el servidor
        axios.get("http://localhost:8081/productos")
            .then((response) => {
                // Almacenar los productos en el estado del componente
                setProductos(response.data);
            })
            .catch((error) => {
                console.error("Error al obtener los productos:", error);
            });
    }, []); // El array vacío [] asegura que esta solicitud se haga solo una vez al cargar el componente

    const toggleCarrito = () => {
        setCarritoAbierto(!carritoAbierto);
    };

    const agregarAlCarrito = (producto, cantidad) => {
        // Implementa la lógica para agregar el producto al carrito con la cantidad deseada
        const productoEnCarrito = carrito.find((item) => item.codigo === producto.codigo);
        if (productoEnCarrito) {
            productoEnCarrito.cantidad += cantidad; // Utiliza la cantidad pasada como argumento
            setCarrito([...carrito]);
        } else {
            setCarrito([...carrito, { ...producto, cantidad }]);
        }
    };

    const eliminarDelCarrito = (producto) => {
        // Implementa la lógica para eliminar el producto del carrito
        const nuevoCarrito = carrito.filter((item) => item.codigo !== producto.codigo);
        setCarrito(nuevoCarrito);
    };

    return (
        <div>
            <h1>Hola, mundo</h1>
            <h2>Productos:</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Precio</th>
                        <th>Stock</th>
                        <th>Cantidad</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {productos.map((producto) => {
                        let cantidad = 1; // Inicializar la cantidad a 1
                        return (
                            <tr key={producto.codigo}>
                                <td>{producto.nombre}</td>
                                <td>${producto.precio}</td>
                                <td>{producto.stock}</td>
                                <td>
                                    <input
                                        type="number"
                                        value={cantidad}
                                        min="1"
                                        max={producto.stock}
                                        onChange={(e) => setCantidad(parseInt(e.target.value))}
                                    />
                                </td>
                                <td>
                                    <button onClick={() => agregarAlCarrito(producto, cantidad)}>Agregar al Carrito</button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>

            <div className="carrito-dropdown">
                <button onClick={toggleCarrito}>Carrito</button>
                {carritoAbierto && (
                    <div className="carrito-contenido">
                        <h3>Carrito de Compras</h3>
                        {carrito.map((producto) => (
                            <div key={producto.codigo}>
                                {producto.nombre} - Precio: ${producto.precio} - Cantidad: {producto.cantidad}
                                <button onClick={() => eliminarDelCarrito(producto)}>Eliminar</button>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}
